import { useMemo } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { 
  User, 
  MapPin, 
  Building2, 
  Euro, 
  Crown, 
  Phone, 
  MessageSquare, 
  Calendar, 
  ChevronLeft, 
  ChevronRight 
} from "lucide-react";
import type { Prospect } from "@shared/schema";

interface PipelineBoardProps {
  prospects: Prospect[];
  onUpdateStatus: (id: string, status: string) => void;
}

const STATUSES = [
  "Nouveau",
  "Contacté", 
  "Qualifié",
  "RDV fixé",
  "Mandat signé",
  "Gagné"
];

const STATUS_COLORS: Record<string, string> = {
  "Nouveau": "bg-gray-100 border-gray-200",
  "Contacté": "bg-blue-50 border-blue-200",
  "Qualifié": "bg-green-50 border-green-200",
  "RDV fixé": "bg-orange-50 border-orange-200",
  "Mandat signé": "bg-purple-50 border-purple-200",
  "Gagné": "bg-emerald-50 border-emerald-200",
};

const STATUS_BADGE_COLORS: Record<string, string> = {
  "Nouveau": "bg-gray-200 text-gray-700",
  "Contacté": "bg-blue-200 text-blue-700",
  "Qualifié": "bg-green-200 text-green-700", 
  "RDV fixé": "bg-orange-200 text-orange-700",
  "Mandat signé": "bg-purple-200 text-purple-700",
  "Gagné": "bg-emerald-200 text-emerald-700",
};

export default function PipelineBoard({ prospects, onUpdateStatus }: PipelineBoardProps) {
  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat("fr-FR", { 
      style: "currency", 
      currency: "EUR",
      minimumFractionDigits: 0 
    }).format(value);
  };

  const columns = useMemo(() => {
    const cols: Record<string, Prospect[]> = {};
    STATUSES.forEach(status => {
      cols[status] = prospects.filter(p => p.statut === status);
    });
    return cols;
  }, [prospects]);

  const moveProspect = (prospect: Prospect, direction: 1 | -1) => {
    const currentIndex = STATUSES.indexOf(prospect.statut!);
    const newIndex = currentIndex + direction;

    if (newIndex >= 0 && newIndex < STATUSES.length) {
      onUpdateStatus(prospect.id, STATUSES[newIndex]);
    }
  };

  const createPhoneLink = (phone: string) => `tel:${phone?.replace(/\s+/g, "")}`;
  const createSMSLink = (phone: string) => `sms:${phone?.replace(/\s+/g, "")}`;

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-6 gap-4" data-testid="pipeline-board">
      {STATUSES.map((status) => (
        <div key={status} className={`rounded-xl p-4 min-h-[500px] ${STATUS_COLORS[status]}`}>
          <div className="flex items-center justify-between mb-4">
            <h3 className="font-semibold text-gray-900" data-testid={`column-title-${status}`}>
              {status}
            </h3>
            <Badge 
              className={STATUS_BADGE_COLORS[status]} 
              data-testid={`column-count-${status}`}
            >
              {columns[status]?.length || 0}
            </Badge>
          </div>

          <div className="space-y-3" data-testid={`column-prospects-${status}`}>
            {columns[status]?.map((prospect) => (
              <Card 
                key={prospect.id} 
                className="cursor-pointer transition-all duration-200 hover:shadow-md border-gray-200 bg-white"
                data-testid={`prospect-card-${prospect.id}`}
              >
                <CardContent className="p-4">
                  <div className="flex items-center justify-between mb-2">
                    <h4 className="font-medium text-gray-900 text-sm line-clamp-1" data-testid={`prospect-name-${prospect.id}`}>
                      {prospect.nomComplet || "Sans nom"}
                    </h4>
                    <div className="flex items-center space-x-1">
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => moveProspect(prospect, -1)}
                        disabled={STATUSES.indexOf(prospect.statut!) === 0}
                        className="h-6 w-6 p-0"
                        data-testid={`button-move-back-${prospect.id}`}
                      >
                        <ChevronLeft className="w-3 h-3" />
                      </Button>
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => moveProspect(prospect, 1)}
                        disabled={STATUSES.indexOf(prospect.statut!) === STATUSES.length - 1}
                        className="h-6 w-6 p-0"
                        data-testid={`button-move-forward-${prospect.id}`}
                      >
                        <ChevronRight className="w-3 h-3" />
                      </Button>
                    </div>
                  </div>

                  <p className="text-xs text-gray-600 mb-3 line-clamp-2" data-testid={`prospect-motivation-${prospect.id}`}>
                    {prospect.motivation || "Aucune motivation renseignée"}
                  </p>

                  <div className="text-xs text-gray-500 mb-3 space-y-1">
                    <div className="flex items-center" data-testid={`prospect-location-${prospect.id}`}>
                      <MapPin className="w-3 h-3 mr-1" />
                      {prospect.ville || "—"}
                    </div>
                    <div className="flex items-center" data-testid={`prospect-property-${prospect.id}`}>
                      <Building2 className="w-3 h-3 mr-1" />
                      {prospect.typeBien || "—"}
                    </div>
                    <div className="flex items-center" data-testid={`prospect-price-${prospect.id}`}>
                      <Euro className="w-3 h-3 mr-1" />
                      {formatCurrency(prospect.prixEstime || prospect.budget || 0)}
                    </div>
                  </div>

                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-1">
                      {prospect.exclusif && (
                        <Badge 
                          variant="outline" 
                          className="text-purple-600 border-purple-200 bg-purple-50 text-xs"
                          data-testid={`prospect-exclusive-${prospect.id}`}
                        >
                          <Crown className="w-3 h-3 mr-1" />
                          Excl.
                        </Badge>
                      )}
                      <Badge 
                        variant="outline" 
                        className="text-xs"
                        data-testid={`prospect-score-${prospect.id}`}
                      >
                        {prospect.score}
                      </Badge>
                    </div>

                    <div className="flex space-x-1">
                      {prospect.telephone && (
                        <>
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={() => window.open(createPhoneLink(prospect.telephone!), "_self")}
                            title="Appeler"
                            className="h-6 w-6 p-0"
                            data-testid={`button-call-${prospect.id}`}
                          >
                            <Phone className="w-3 h-3 text-green-600" />
                          </Button>
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={() => window.open(createSMSLink(prospect.telephone!), "_self")}
                            title="SMS"
                            className="h-6 w-6 p-0"
                            data-testid={`button-sms-${prospect.id}`}
                          >
                            <MessageSquare className="w-3 h-3 text-blue-600" />
                          </Button>
                        </>
                      )}
                    </div>
                  </div>

                  {prospect.prochaineAction && (
                    <div className="mt-2 pt-2 border-t border-gray-100">
                      <div className="flex items-center text-xs text-gray-500" data-testid={`prospect-next-action-${prospect.id}`}>
                        <Calendar className="w-3 h-3 mr-1" />
                        {new Date(prospect.prochaineAction).toLocaleDateString("fr-FR")}
                      </div>
                    </div>
                  )}
                </CardContent>
              </Card>
            ))}

            {columns[status]?.length === 0 && (
              <div className="text-center text-gray-400 py-8" data-testid={`empty-column-${status}`}>
                <User className="w-8 h-8 mx-auto mb-2 opacity-50" />
                <p className="text-sm">Aucun prospect</p>
              </div>
            )}
          </div>
        </div>
      ))}
    </div>
  );
}

client/src/components/crm/prospect-table.content:
import { useState } from "react";
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from "@/components/ui/table";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { 
  Phone, 
  MessageSquare, 
  MessageCircle, 
  Calendar, 
  Edit, 
  Trash2, 
  User, 
  Crown,
  AlertTriangle,
  FileDown,
  ExternalLink
} from "lucide-react";
import HotLeadBadge from "./hot-lead-badge";
import type { Prospect } from "@shared/schema";

// Enhanced CRM helper functions
const isReadyToSell = (prospect: any): boolean => {
  return !!(prospect.telephone && 
           prospect.consentement &&
           prospect.intention &&
           prospect.timeline &&
           prospect.ville &&
           (prospect.budget || prospect.prixEstime) &&
           (prospect.liveTouches || 0) >= 1);
};

const isHotLead = (prospect: any): boolean => {
  const score = prospect.score || 0;
  const timeline = prospect.timeline || "";
  const timelineMonths = parseInt(timeline.split(' ')[0]) || 12;
  return score > 80 && timelineMonths < 3;
};

interface ProspectTableProps {
  prospects: Prospect[];
  onEdit: (prospect: Prospect) => void;
  onDelete: (id: string) => void;
  compact?: boolean;
}

export default function ProspectTable({ prospects, onEdit, onDelete, compact = false }: ProspectTableProps) {
  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat("fr-FR", {
      style: "currency",
      currency: "EUR",
      minimumFractionDigits: 0,
    }).format(value);
  };

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      "Nouveau": "bg-gray-100 text-gray-800",
      "Contacté": "bg-blue-100 text-blue-800",
      "Qualifié": "bg-green-100 text-green-800",
      "RDV fixé": "bg-orange-100 text-orange-800",
      "Mandate Pending": "bg-yellow-100 text-yellow-800", // New status
      "Mandat signé": "bg-purple-100 text-purple-800",
      "Mandate Signed": "bg-purple-100 text-purple-800", // Alternative naming
      "En négociation": "bg-amber-100 text-amber-800",
      "Gagné": "bg-emerald-100 text-emerald-800",
      "Won": "bg-emerald-100 text-emerald-800",
      "Perdu": "bg-red-100 text-red-800",
      "Lost": "bg-red-100 text-red-800",
      "Pas de réponse": "bg-gray-100 text-gray-600",
    };
    return colors[status] || "bg-gray-100 text-gray-800";
  };

  // WhatsApp link generator
  const generateWhatsAppLink = (prospect: Prospect) => {
    const message = `Bonjour ${prospect.nomComplet}, je suis votre agent immobilier concernant votre projet ${prospect.type?.toLowerCase()} à ${prospect.ville}. Budget: ${formatCurrency(prospect.budget || 0)}. Pouvons-nous discuter de votre projet ?`;
    const phoneNumber = prospect.telephone?.replace(/\s/g, '').replace(/\+33/, '33');
    return `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;
  };

  // Export PDF functionality
  const exportProspectPDF = (prospect: Prospect) => {
    // This would integrate with jsPDF
    console.log("Exporting PDF for prospect:", prospect.id);
    // Implementation would go here
  };

  const getTypeColor = (type: string) => {
    return type === "Vendeur" ? "bg-blue-100 text-blue-800" : "bg-purple-100 text-purple-800";
  };

  const calculateExpectedValue = (prospect: Prospect) => {
    const price = prospect.prixEstime || prospect.budget || 0;
    const rate = prospect.tauxHonoraires || 0.04;
    const probability = getStatusProbability(prospect.statut!);
    const exclusiveBonus = prospect.exclusif ? 1.1 : 1;
    return price * rate * probability * exclusiveBonus;
  };

  const getStatusProbability = (status: string): number => {
    const probabilities: Record<string, number> = {
      "Nouveau": 0.05,
      "Contacté": 0.1,
      "Qualifié": 0.25,
      "RDV fixé": 0.5,
      "Mandat signé": 0.9,
      "Gagné": 1,
      "Perdu": 0,
      "Pas de réponse": 0.02,
    };
    return probabilities[status] || 0;
  };

  const createPhoneLink = (phone: string) => `tel:${phone?.replace(/\s+/g, "")}`;
  const createSMSLink = (phone: string) => `sms:${phone?.replace(/\s+/g, "")}`;
  const createWhatsAppLink = (phone: string) => 
    `https://wa.me/${phone?.replace(/[\s+\-]/g, "")}`;

  if (prospects.length === 0) {
    return (
      <Card>
        <CardContent className="py-12">
          <div className="text-center">
            <User className="w-16 h-16 mx-auto text-gray-400 mb-4" />
            <p className="text-gray-600">Aucun prospect trouvé</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (compact) {
    return (
      <div className="grid gap-4" data-testid="prospect-cards">
        {prospects.map((prospect) => (
          <Card key={prospect.id} className="transition-all duration-200 hover:shadow-md">
            <CardContent className="p-6">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center space-x-3">
                  <div className="w-10 h-10 bg-primary-100 rounded-full flex items-center justify-center">
                    <User className="w-5 h-5 text-primary-600" />
                  </div>
                  <div>
                    <div className="flex items-center gap-2">
                      <h3 className="font-semibold text-gray-900" data-testid={`prospect-name-${prospect.id}`}>
                        {prospect.nomComplet || "Sans nom"}
                      </h3>
                      {isHotLead(prospect) && <HotLeadBadge />}
                      {isReadyToSell(prospect) && (
                        <Badge variant="outline" className="bg-green-50 text-green-700 border-green-200">
                          ✓ Ready
                        </Badge>
                      )}
                    </div>
                    <p className="text-sm text-gray-500">
                      {prospect.type} • {prospect.typeBien} • {prospect.ville}
                    </p>
                  </div>
                </div>
                <div className="text-right">
                  <div className="text-lg font-bold text-green-600" data-testid={`prospect-value-${prospect.id}`}>
                    {formatCurrency(calculateExpectedValue(prospect))}
                  </div>
                  <div className="text-sm text-gray-500">Valeur attendue</div>
                </div>
              </div>

              <div className="flex items-center justify-between text-sm text-gray-600 mb-4">
                <Badge className={getStatusColor(prospect.statut!)} data-testid={`prospect-status-${prospect.id}`}>
                  {prospect.statut}
                </Badge>
                {prospect.exclusif && (
                  <span className="flex items-center text-purple-600">
                    <Crown className="w-4 h-4 mr-1" />
                    Exclusif
                  </span>
                )}
                <span data-testid={`prospect-score-${prospect.id}`}>Score: {prospect.score}</span>
              </div>

              {prospect.motivation && (
                <p className="text-sm text-gray-600 mb-4 line-clamp-2" data-testid={`prospect-motivation-${prospect.id}`}>
                  {prospect.motivation}
                </p>
              )}

              <div className="flex items-center justify-between">
                <div className="text-sm text-gray-500">
                  {prospect.prochaineAction ? (
                    <span data-testid={`prospect-next-action-${prospect.id}`}>
                      Prochaine action: {new Date(prospect.prochaineAction).toLocaleDateString("fr-FR")}
                    </span>
                  ) : (
                    "Aucune action planifiée"
                  )}
                </div>
                <div className="flex space-x-1">
                  {prospect.telephone && (
                    <>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => window.open(createPhoneLink(prospect.telephone!), "_self")}
                        data-testid={`button-call-${prospect.id}`}
                      >
                        <Phone className="w-4 h-4" />
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => window.open(createSMSLink(prospect.telephone!), "_self")}
                        data-testid={`button-sms-${prospect.id}`}
                      >
                        <MessageSquare className="w-4 h-4" />
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => window.open(createWhatsAppLink(prospect.telephone!), "_blank")}
                        data-testid={`button-whatsapp-${prospect.id}`}
                      >
                        <MessageCircle className="w-4 h-4" />
                      </Button>
                    </>
                  )}
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => onEdit(prospect)}
                    data-testid={`button-edit-${prospect.id}`}
                  >
                    <Edit className="w-4 h-4" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  return (
    <Card data-testid="prospect-table">
      <CardHeader>
        <CardTitle>Liste des Prospects ({prospects.length})</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="overflow-x-auto">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Contact</TableHead>
                <TableHead>Type</TableHead>
                <TableHead>Statut</TableHead>
                <TableHead>Valeur</TableHead>
                <TableHead>Score</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {prospects.map((prospect) => (
                <TableRow key={prospect.id} className="hover:bg-gray-50">
                  <TableCell>
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-primary-100 rounded-full flex items-center justify-center">
                        <User className="w-5 h-5 text-primary-600" />
                      </div>
                      <div>
                        <div className="flex items-center gap-2">
                          <div className="font-medium text-gray-900" data-testid={`prospect-name-${prospect.id}`}>
                            {prospect.nomComplet || "Sans nom"}
                          </div>
                          {isHotLead(prospect) && <HotLeadBadge />}
                          {isReadyToSell(prospect) && (
                            <Badge variant="outline" className="bg-green-50 text-green-700 border-green-200 text-xs">
                              ✓ Ready
                            </Badge>
                          )}
                        </div>
                        <div className="text-sm text-gray-500" data-testid={`prospect-contact-${prospect.id}`}>
                          {prospect.email && <div>{prospect.email}</div>}
                          {prospect.telephone && <div>{prospect.telephone}</div>}
                        </div>
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge className={getTypeColor(prospect.type!)} data-testid={`prospect-type-${prospect.id}`}>
                      {prospect.type}
                    </Badge>
                    <div className="text-sm text-gray-500 mt-1">
                      {prospect.typeBien} • {prospect.ville}
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge className={getStatusColor(prospect.statut!)} data-testid={`prospect-status-${prospect.id}`}>
                      {prospect.statut}
                    </Badge>
                    {prospect.exclusif && (
                      <div className="flex items-center text-purple-600 text-xs mt-1">
                        <Crown className="w-3 h-3 mr-1" />
                        Exclusif
                      </div>
                    )}
                  </TableCell>
                  <TableCell>
                    <div className="font-medium text-gray-900" data-testid={`prospect-value-${prospect.id}`}>
                      {formatCurrency(calculateExpectedValue(prospect))}
                    </div>
                    <div className="text-sm text-gray-500">
                      Prix: {formatCurrency(prospect.prixEstime || prospect.budget || 0)}
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center space-x-2">
                      <span className="font-medium" data-testid={`prospect-score-${prospect.id}`}>
                        {prospect.score}
                      </span>
                      <div className="w-16 bg-gray-200 rounded-full h-2">
                        <div 
                          className={`h-2 rounded-full ${
                            prospect.score! >= 80 ? 'bg-green-500' : 
                            prospect.score! >= 60 ? 'bg-yellow-500' : 'bg-red-500'
                          }`}
                          style={{ width: `${Math.min(prospect.score!, 100)}%` }}
                        />
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="flex space-x-1">
                      {prospect.telephone ? (
                        <>
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={() => window.open(createPhoneLink(prospect.telephone!), "_self")}
                            title="Appeler"
                            data-testid={`button-call-${prospect.id}`}
                          >
                            <Phone className="w-4 h-4 text-green-600" />
                          </Button>
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={() => window.open(createSMSLink(prospect.telephone!), "_self")}
                            title="SMS"
                            data-testid={`button-sms-${prospect.id}`}
                          >
                            <MessageSquare className="w-4 h-4 text-blue-600" />
                          </Button>
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={() => window.open(createWhatsAppLink(prospect.telephone!), "_blank")}
                            title="WhatsApp"
                            data-testid={`button-whatsapp-${prospect.id}`}
                          >
