import { Badge } from "@/components/ui/badge";
import { Flame } from "lucide-react";
import type { Prospect } from "@shared/schema";

interface HotLeadBadgeProps {
  prospect: Prospect;
}

export default function HotLeadBadge({ prospect }: HotLeadBadgeProps) {
  // Auto Hot Lead logic: score > 80 and timeline < 3 months
  const isHotLead = prospect.score && prospect.score > 80 && 
    prospect.timeline && 
    ["1 mois", "2 mois", "moins de 3 mois", "urgent"].includes(prospect.timeline.toLowerCase());

  if (!isHotLead && !prospect.isHotLead) return null;

  return (
    <Badge variant="destructive" className="bg-red-500 hover:bg-red-600 text-white animate-pulse">
      <Flame className="w-3 h-3 mr-1" />
      Hot Lead
    </Badge>
  );
}

client/src/components/crm/mobile-express.content:
import { useState, useEffect, useRef } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Phone, MessageCircle, Calendar, MapPin, Euro, User, ArrowRight, ChevronLeft, ChevronRight } from "lucide-react";
import type { Prospect } from "@shared/schema";

interface MobileExpressModeProps {
  prospects: Prospect[];
  onCall: (prospect: Prospect) => void;
  onWhatsApp: (prospect: Prospect) => void;
  onScheduleRDV: (prospect: Prospect) => void;
}

export default function MobileExpressMode({ prospects, onCall, onWhatsApp, onScheduleRDV }: MobileExpressModeProps) {
  const [currentIndex, setCurrentIndex] = useState(() => {
    return parseInt(sessionStorage.getItem('mobile-express-index') || '0');
  });
  const cardRef = useRef<HTMLDivElement>(null);
  const startX = useRef(0);
  const isDragging = useRef(false);

  // Persist current index
  useEffect(() => {
    sessionStorage.setItem('mobile-express-index', currentIndex.toString());
  }, [currentIndex]);

  // Filter prospects that need immediate attention
  const priorityProspects = prospects.filter(p => {
    const today = new Date().toDateString();
    const needsCall = p.prochaineAction && 
      new Date(p.prochaineAction).toDateString() === today &&
      !["Gagné", "Perdu", "Pas de réponse"].includes(p.statut || "");

    const isHotLead = (p.score && p.score > 80) || p.isHotLead;
    const isNewToday = p.creeLe && new Date(p.creeLe).toDateString() === today;

    return needsCall || isHotLead || isNewToday;
  }).sort((a, b) => (b.score || 0) - (a.score || 0));

  const currentProspect = priorityProspects[currentIndex];

  const nextProspect = () => {
    setCurrentIndex((prev) => (prev + 1) % priorityProspects.length);
  };

  const prevProspect = () => {
    setCurrentIndex((prev) => (prev - 1 + priorityProspects.length) % priorityProspects.length);
  };

  // Touch handlers for swipe functionality
  const handleTouchStart = (e: React.TouchEvent) => {
    startX.current = e.touches[0].clientX;
    isDragging.current = true;
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    if (!isDragging.current) return;
    e.preventDefault();
  };

  const handleTouchEnd = (e: React.TouchEvent) => {
    if (!isDragging.current) return;
    isDragging.current = false;

    const endX = e.changedTouches[0].clientX;
    const diffX = startX.current - endX;
    const minSwipeDistance = 50;

    if (Math.abs(diffX) > minSwipeDistance) {
      if (diffX > 0) {
        // Swiped left - next prospect
        nextProspect();
      } else {
        // Swiped right - previous prospect
        prevProspect();
      }
    }
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat("fr-FR", {
      style: "currency",
      currency: "EUR",
      minimumFractionDigits: 0,
    }).format(value);
  };

  const getUrgencyColor = (prospect: Prospect) => {
    if (prospect.score && prospect.score > 90) return "bg-red-500";
    if (prospect.score && prospect.score > 80) return "bg-orange-500";
    if (prospect.prochaineAction && new Date(prospect.prochaineAction).toDateString() === new Date().toDateString()) {
      return "bg-blue-500";
    }
    return "bg-green-500";
  };

  if (priorityProspects.length === 0) {
    return (
      <Card className="max-w-md mx-auto">
        <CardContent className="pt-6">
          <div className="text-center">
            <User className="w-16 h-16 mx-auto text-gray-400 mb-4" />
            <h3 className="text-lg font-medium mb-2">Aucun prospect prioritaire</h3>
            <p className="text-gray-500">Tous vos prospects sont à jour !</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="max-w-md mx-auto space-y-4">
      {/* Progress Indicator */}
      <div className="flex items-center justify-between text-sm text-gray-600">
        <span>Prospect {currentIndex + 1} sur {priorityProspects.length}</span>
        <div className="flex gap-1">
          {priorityProspects.map((_, index) => (
            <div
              key={index}
              className={`w-2 h-2 rounded-full ${
                index === currentIndex ? "bg-blue-500" : "bg-gray-300"
              }`}
            />
          ))}
        </div>
      </div>

      {/* Main Prospect Card */}
      <Card 
        ref={cardRef}
        className="relative overflow-hidden select-none"
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
        onTouchEnd={handleTouchEnd}
      >
        <div className={`absolute top-0 left-0 w-1 h-full ${getUrgencyColor(currentProspect)}`}></div>

        {/* Swipe indicators */}
        <div className="absolute top-4 right-4 flex items-center space-x-1">
          <Button 
            size="sm" 
            variant="ghost" 
            onClick={prevProspect}
            disabled={priorityProspects.length <= 1}
            className="h-8 w-8 p-0 mobile-button"
            title="Prospect précédent"
          >
            <ChevronLeft className="h-4 w-4" />
          </Button>
          <Button 
            size="sm" 
            variant="ghost" 
            onClick={nextProspect}
            disabled={priorityProspects.length <= 1}
            className="h-8 w-8 p-0 mobile-button"
            title="Prospect suivant"
          >
            <ChevronRight className="h-4 w-4" />
          </Button>
        </div>

        <CardHeader className="pb-4">
          <div className="flex items-start justify-between">
            <div>
              <CardTitle className="text-xl">{currentProspect.nomComplet}</CardTitle>
              <div className="flex items-center gap-2 mt-1">
                <MapPin className="w-4 h-4 text-gray-500" />
                <span className="text-gray-600">{currentProspect.ville}</span>
              </div>
            </div>
            <div className="text-right">
              <Badge variant="outline" className="mb-1">
                {currentProspect.type}
              </Badge>
              {currentProspect.score && (
                <div className="text-sm text-gray-500">
                  Score: {currentProspect.score}/100
                </div>
              )}
            </div>
          </div>
        </CardHeader>

        <CardContent className="space-y-4">
          {/* Key Info */}
          <div className="grid grid-cols-2 gap-4 text-sm">
            <div>
              <span className="text-gray-500">Budget:</span>
              <p className="font-medium">
                {formatCurrency(currentProspect.budget || currentProspect.prixEstime || 0)}
              </p>
            </div>
            <div>
              <span className="text-gray-500">Statut:</span>
              <p className="font-medium">{currentProspect.statut}</p>
            </div>
            <div>
              <span className="text-gray-500">Source:</span>
              <p className="font-medium">{currentProspect.exactSource || currentProspect.source || "—"}</p>
            </div>
            <div>
              <span className="text-gray-500">Timeline:</span>
              <p className="font-medium">{currentProspect.timeline || "—"}</p>
            </div>
          </div>

          {/* Notes */}
          {currentProspect.notes && (
            <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-3">
              <span className="text-gray-500 text-sm">Notes:</span>
              <p className="text-sm mt-1">{currentProspect.notes}</p>
            </div>
          )}

          {/* Action Buttons */}
          <div className="grid grid-cols-3 gap-2">
            <Button
              onClick={() => onCall(currentProspect)}
              className="flex flex-col gap-1 h-16 mobile-button"
              variant="outline"
              title={`Appeler ${currentProspect.nomComplet}`}
            >
              <Phone className="w-5 h-5" />
              <span className="text-xs">Appeler</span>
            </Button>

            <Button
              onClick={() => onWhatsApp(currentProspect)}
              className="flex flex-col gap-1 h-16 mobile-button"
              variant="outline"
              title={`Contacter ${currentProspect.nomComplet} par WhatsApp`}
            >
              <MessageCircle className="w-5 h-5" />
              <span className="text-xs">WhatsApp</span>
            </Button>

            <Button
              onClick={() => onScheduleRDV(currentProspect)}
              className="flex flex-col gap-1 h-16 mobile-button"
              variant="outline"
              title={`Programmer RDV avec ${currentProspect.nomComplet}`}
            >
              <Calendar className="w-5 h-5" />
              <span className="text-xs">RDV</span>
            </Button>
          </div>

          {/* Navigation hint */}
          <div className="text-center text-xs text-gray-400 mt-2">
            Glissez ← → pour naviguer
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/crm/notifications-.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Bell, Phone, Calendar, AlertTriangle, CheckCircle, X } from "lucide-react";
import { format } from "date-fns";
import { fr } from "date-fns/locale";
import type { Prospect } from "@shared/schema";

interface NotificationsPanelProps {
  prospects: Prospect[];
}

interface Notification {
  id: string;
  type: "call_due" | "follow_up" | "hot_lead" | "rdv_reminder";
  title: string;
  message: string;
  prospect?: Prospect;
  timestamp: Date;
  isRead: boolean;
}

export default function NotificationsPanel({ prospects }: NotificationsPanelProps) {
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [showNotifications, setShowNotifications] = useState(false);

  useEffect(() => {
    const generateNotifications = () => {
      const now = new Date();
      const today = now.toDateString();
      const newNotifications: Notification[] = [];

      // Call due notifications
      prospects.forEach(prospect => {
        if (prospect.prochaineAction && 
            new Date(prospect.prochaineAction).toDateString() === today &&
            !["Gagné", "Perdu", "Pas de réponse"].includes(prospect.statut || "")) {
          newNotifications.push({
            id: `call_${prospect.id}`,
            type: "call_due",
            title: "Appel à effectuer",
            message: `${prospect.nomComplet} - ${prospect.ville}`,
            prospect,
            timestamp: new Date(prospect.prochaineAction),
            isRead: false
          });
        }

        // Hot lead notifications
        if (prospect.score && prospect.score > 80 && 
            prospect.timeline && 
            ["1 mois", "2 mois", "moins de 3 mois", "urgent"].includes(prospect.timeline.toLowerCase())) {
          newNotifications.push({
            id: `hot_${prospect.id}`,
            type: "hot_lead",
            title: "Hot Lead détecté",
            message: `${prospect.nomComplet} - Score: ${prospect.score}`,
            prospect,
            timestamp: now,
            isRead: false
          });
        }

        // Follow-up needed
        if (prospect.dernierContact) {
          const daysSinceContact = Math.floor((now.getTime() - new Date(prospect.dernierContact).getTime()) / (1000 * 60 * 60 * 24));
          if (daysSinceContact > 7 && !["Gagné", "Perdu", "Pas de réponse"].includes(prospect.statut || "")) {
            newNotifications.push({
              id: `followup_${prospect.id}`,
              type: "follow_up",
              title: "Relance nécessaire",
              message: `${prospect.nomComplet} - Dernier contact: ${daysSinceContact} jours`,
              prospect,
              timestamp: now,
              isRead: false
            });
          }
        }
      });

      setNotifications(newNotifications.slice(0, 10)); // Limit to 10 notifications
    };

    generateNotifications();

    // Update notifications every minute
    const interval = setInterval(generateNotifications, 60000);
    return () => clearInterval(interval);
  }, [prospects]);

  const unreadCount = notifications.filter(n => !n.isRead).length;

  const markAsRead = (id: string) => {
    setNotifications(prev => 
      prev.map(n => n.id === id ? { ...n, isRead: true } : n)
    );
  };

  const markAllAsRead = () => {
    setNotifications(prev => prev.map(n => ({ ...n, isRead: true })));
  };

  const getIcon = (type: string) => {
    switch (type) {
      case "call_due": return <Phone className="w-4 h-4 text-blue-600" />;
      case "hot_lead": return <AlertTriangle className="w-4 h-4 text-red-600" />;
      case "follow_up": return <Calendar className="w-4 h-4 text-orange-600" />;
      default: return <Bell className="w-4 h-4" />;
    }
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case "call_due": return "bg-blue-100 text-blue-800 border-blue-200";
      case "hot_lead": return "bg-red-100 text-red-800 border-red-200";
      case "follow_up": return "bg-orange-100 text-orange-800 border-orange-200";
      default: return "bg-gray-100 text-gray-800 border-gray-200";
    }
  };

  return (
    <div className="relative">
      <Button
        variant="outline"
        size="sm"
        onClick={() => setShowNotifications(!showNotifications)}
        className="relative"
      >
        <Bell className="w-4 h-4" />
        {unreadCount > 0 && (
          <Badge className="absolute -top-1 -right-1 h-5 w-5 rounded-full p-0 text-xs bg-red-500">
            {unreadCount > 9 ? "9+" : unreadCount}
          </Badge>
        )}
      </Button>

      {showNotifications && (
        <Card className="absolute right-0 top-full mt-2 w-80 max-h-96 overflow-hidden z-50 shadow-lg">
          <CardHeader className="pb-2">
            <div className="flex items-center justify-between">
              <CardTitle className="text-lg">Notifications</CardTitle>
              <div className="flex gap-2">
                {unreadCount > 0 && (
                  <Button variant="ghost" size="sm" onClick={markAllAsRead}>
                    <CheckCircle className="w-4 h-4 mr-1" />
                    Tout lire
                  </Button>
                )}
                <Button variant="ghost" size="sm" onClick={() => setShowNotifications(false)}>
                  <X className="w-4 h-4" />
                </Button>
              </div>
            </div>
          </CardHeader>
          <CardContent className="p-0">
            <div className="max-h-64 overflow-y-auto">
              {notifications.length === 0 ? (
                <p className="text-center text-gray-500 py-8">Aucune notification</p>
              ) : (
                notifications.map((notification) => (
                  <div
                    key={notification.id}
                    className={`p-3 border-b cursor-pointer hover:bg-gray-50 ${
                      !notification.isRead ? "bg-blue-50" : ""
                    }`}
                    onClick={() => markAsRead(notification.id)}
                  >
                    <div className="flex items-start gap-3">
                      <div className="flex-shrink-0 mt-1">
                        {getIcon(notification.type)}
                      </div>
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center gap-2 mb-1">
                          <span className="font-medium text-sm">{notification.title}</span>
                          <Badge className={`text-xs ${getTypeColor(notification.type)}`}>
                            {notification.type === "call_due" ? "Appel" :
                             notification.type === "hot_lead" ? "Hot" :
                             notification.type === "follow_up" ? "Relance" : "Info"}
                          </Badge>
                        </div>
                        <p className="text-sm text-gray-700">{notification.message}</p>
                        <p className="text-xs text-gray-500 mt-1">
                          {format(notification.timestamp, "dd MMM à HH:mm", { locale: fr })}
                        </p>
                      </div>
                      {!notification.isRead && (
                        <div className="w-2 h-2 bg-blue-500 rounded-full flex-shrink-0 mt-2"></div>
                      )}
                    </div>
                  </div>
                ))
              )}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

