        </Button>
        <Button size="sm" variant="outline" onClick={handleWhatsApp} title="WhatsApp">
          <MessageCircle className="w-3 h-3" />
        </Button>
        <Button size="sm" variant="outline" onClick={handleEmail} title="Email">
          <Mail className="w-3 h-3" />
        </Button>
      </div>
